import React, { useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import axios from 'axios';
import { Modal, } from "react-bootstrap";
import { 
    NormalBrandType,
    NormalBrandTypeLabel,
    LuxuryBrandType, 
    LuxyryBrandTypeLabel 
  } from "../Constants/Brand/BrandConstants";
  
  const Info = ({ brand, handleClose }) => {
    const getBrandTypeName = (id) => {
      return id == LuxuryBrandType ? LuxyryBrandTypeLabel : NormalBrandTypeLabel;
    }
    const [name, setName] = useState("");
    const [quantity, setQuantity] = useState('');
    const [originalPrice, setOriginalPrice] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [timeCreate, setTimeCreate] = useState('');
    const [status, setStatus] = useState('');
    const [viewCount, setViewCount] = useState('');
    const [details, setDetails] = useState('');
    const [thumbnailImage, setThumbnailImage] = useState('');
    const postData = async () => {
         await axios.post("https://localhost:5000/api/Products", {
            name,
            quantity,
            originalPrice,
            price,
            description,
            timeCreate,
            status,
            viewCount,
            details,
            thumbnailImage
        })
    }
    return (
      <>
          <Form className="create-form">
                <Form.Field>
                    <label>First Name</label>
                    <input placeholder='First Name' onChange={(e) => setName(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Quantity</label>
                    <input placeholder='Quantity' onChange={(e) => setQuantity(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>OriginalPrice</label>
                    <input placeholder='OriginalPrice' onChange={(e) => setOriginalPrice(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Price</label>
                    <input placeholder='Price' onChange={(e) => setPrice(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Description</label>
                    <input placeholder='Description' onChange={(e) => setDescription(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>TimeCreate</label>
                    <input placeholder='TimeCreate' onChange={(e) => setTimeCreate(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Status</label>
                    <input placeholder='Status' onChange={(e) => setStatus(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>ViewCount</label>
                    <input placeholder='ViewCount' onChange={(e) => setViewCount(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Details</label>
                    <input placeholder='Details' onChange={(e) => setDetails(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>ThumbnailImage</label>
                    <img src={brand.imagePath} className='object-center w-full rounded-md'  onChange={(e) => setThumbnailImage(e.target.value)}/>
                </Form.Field>
                <Button onClick={postData} type='submit'>Submit</Button>
            </Form>
      </>
    );
  }
  
  export default Info;
