import React, {Component,  useState } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import axios from 'axios';
import{ createProduct } from '../services/productCreateService'
export default function Create() {
    const [Name, setName] = useState("");
    const [Quantity, setQuantity] = useState('');
    const [OriginalPrice, setOriginalPrice] = useState('');
    const [Price, setPrice] = useState('');
    const [Description, setDescription] = useState('');
    const [TimeCreate, setTimeCreate] = useState('');
    const [Status, setStatus] = useState('');
    const [ViewCount, setViewCount] = useState('');
    const [Details, setDetails] = useState('');
    const [ThumbnailImage, setThumbnailImage] = useState('');
    const postData = async () => {
         await axios.post(createProduct, {
            Name : '123',
            Quantity : '123',
            originalPrice : '123',
            Price : '123',
            Description : "123",
            TimeCreate : '2022-03-13 06:44:46.0820524',
            Status : "2",
            ViewCount : '123',
            Details : '123',
            ThumbnailImage : "@213095366_800869210611260_5946658771428924273_n.png;type=image/png"
        })
    }
    // const [listCate,setListCate] =useState();
    // useEffect(async()=>{
    //     const data = await add().then(res=>setListCate(res)) 
    // },[])
    const [cate,setCate]=useState( {
        ProductName : '123',
        Quantity : '123',
        originalPrice : '123',
        Price : '123',
        Description : "123",
        TimeCreate : '2022-03-13 06:44:46.0820524',
        Status : "0",
        ViewCount : '123',
        Details : '123',
        ThumbnailImage : "@213095366_800869210611260_5946658771428924273_n.png;type=image/png"
    })
    const handleChange = e => {
        const { name, value } = e.target;
        setCate(prevState => ({
            ...prevState,
            [name]: value
        }));
        console.log(cate)

    };
    const btnOnClick=async ()=>{
        console.log(cate)
        const response = await axios.post(createProduct,cate) 
        // const get = await axios.get("https://localhost:5011/api/Category")
        console.log(response)
    }

    console.log(cate);
    return (
        <div>
            <Form className="create-form">
                <Form.Field>
                    <label>First Name</label>
                    <input placeholder='First Name' onChange={(e) => setName(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Quantity</label>
                    <input placeholder='Quantity' onChange={(e) => setQuantity(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>OriginalPrice</label>
                    <input placeholder='OriginalPrice' onChange={(e) => setOriginalPrice(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Price</label>
                    <input placeholder='Price' onChange={(e) => setPrice(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Description</label>
                    <input placeholder='Description' onChange={(e) => setDescription(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>TimeCreate</label>
                    <input placeholder='TimeCreate' onChange={(e) => setTimeCreate(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Status</label>
                    <input placeholder='Status' onChange={(e) => setStatus(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>ViewCount</label>
                    <input placeholder='ViewCount' onChange={(e) => setViewCount(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Details</label>
                    <input placeholder='Details' onChange={(e) => setDetails(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>ThumbnailImage</label>
                    <input placeholder='setThumbnailImage' onChange={(e) => setThumbnailImage(e.target.value)}/>
                </Form.Field>
                <button onClick={btnOnClick}>Xác nhận</button>

                <Button onClick={postData} type='submit'>Submit</Button>
            </Form>
        </div>
    )
}
