import React, { useState } from 'react';
import axios from 'axios';

export default  function AddCategory() {
    // const [listCate,setListCate] =useState();
    // useEffect(async()=>{
    //     const data = await add().then(res=>setListCate(res)) 
    // },[])
    const [cate,setCate]=useState( {
        Name : "123",
        Quantity : '123',
        originalPrice : '123',
        Price : '123',
        Description : "123",
        TimeCreate : '123',
        Status : "2",
        ViewCount : '123',
        Details : '123',
        ThumbnailImage : "123"
    })
    // console.log(listCate)
    const handleChange = e => {
        const { name, value } = e.target;
        setCate(prevState => ({
            ...prevState,
            [name]: value
        }));
        console.log(cate)

    };
    const btnOnClick=async ()=>{
        console.log(cate)
        const response = await axios.post("https://localhost:5000/api/Products",cate) 

        console.log(response)
    }
    return (
        <div>
        <input
            value={cate.categoryName}
            type="text"
            onChange={handleChange}
            name="categoryName"
        />
       <input
            value={cate.categoryPicture}
            type="file"
            accept="image/png, image/jpeg"
            onChange={handleChange}
            name="categoryPicture"
        />
        <button onClick={btnOnClick}>Xác nhận</button>
            </div>
    );
}
